			SELECT CASE WHEN ISNULL(cs.category,0) = 0 OR ISNULL(parentScreen.groupscreens,0) = 0
				THEN t.tableName
				ELSE ISNULL(dbo.udfsys_getcategory(cs.category),t.tableName)
