CREATE VIEW [dbo].[ASRSysColumns]
					WITH SCHEMABINDING
					AS SELECT base.[columnid], base.[tableid], base.[columntype], base.[datatype], base.[defaultvalue], base.[size], base.[decimals], base.[lookuptableid], base.[lookupcolumnid], base.[controltype], base.[spinnerminimum], base.[spinnermaximum], base.[spinnerincrement], base.[audit], base.[duplicate], base.[mandatory], base.[uniquecheck], base.[convertcase], base.[mask], base.[alphaonly], base.[blankifzero], base.[multiline], base.[alignment], base.[calcexprid], base.[gotfocusexprid], base.[lostfocusexprid], base.[calctrigger], base.[readonly], base.[statusbarmessage], base.[errormessage], base.[linktableid], base.[afdenabled], base.[afdindividual], base.[afdforename], base.[afdsurname], base.[afdinitial], base.[afdtelephone], base.[afdaddress], base.[afdproperty], base.[afdstreet], base.[afdlocality], base.[afdtown], base.[afdcounty], base.[dfltvalueexprid], base.[linkorderid], base.[oleonserver], base.[childuniquecheck], base.[linkviewid], base.[defaultdisplaywidth], base.[columnname], base.[uniquechecktype], base.[trimming], base.[use1000separator], base.[lookupfiltercolumnid], base.[lookupfiltervalueid], base.[qaddressenabled], base.[qaindividual], base.[qaaddress], base.[qaproperty], base.[qastreet], base.[qalocality], base.[qatown], base.[qacounty], base.[lookupfilteroperator], base.[embedded], base.[oletype], base.[maxolesizeenabled], base.[maxolesize], base.[autoupdatelookupvalues], base.[calculateifempty],  obj.[locked], obj.[lastupdated], obj.[lastupdatedby]
						FROM dbo.[tbsys_columns] base
						INNER JOIN dbo.[tbsys_scriptedobjects] obj ON obj.targetid = base.columnid AND obj.objecttype = 2
						INNER JOIN dbo.[tbstat_effectivedates] dt ON dt.[type] = 1
						WHERE obj.effectivedate <= dt.[date]