				AND L.startOutboundFlowCode = @piValue
				AND E.type = 8 -- 8 = Connector 1
		
			OPEN succeedingConnectorsCursor
			FETCH NEXT FROM succeedingConnectorsCursor INTO @iConnectorPairID
